command:example.dev:code:
$connector = get-connector | where-object {$_.displayname -match 'test connector'}
$runAsAccount = get-runasaccount | where-object {$_.username -eq 'asttest' -and $_.type -eq 'Windows'}
$tier = new-tier -name 'test tier' ` -managementServerName 'fabrikam.contoso.net' -runAsAccount $runAsAccount
add-connectortotier -connector $connector -tier $tier

$connector = get-connector |
where-object {$_.displayname -eq 'test connector'}
$tier = get-tier | where-object {$_.name -eq 'Test Tier'}
remove-connectorfromtier -connector $connector -tier $tier

$alerts = get-alert |
where-object {$_.ResolutionState -eq 0}

foreach($alert in $alerts)
{
   resolve-alert -comment "Resolving Alert" -Alert $alert
}
